document.addEventListener("DOMContentLoaded",()=>{var e=document.querySelector(".form-state");const r=document.querySelector(".box-form");var t=document.querySelector(".box-form__error .btn");const s=$(e).parsley();e.addEventListener("submit",e=>{e.preventDefault(),s.validate(),s.isValid()?(r.classList.add("form-success"),r.classList.remove("form-error")):(r.classList.add("form-error"),r.classList.remove("form-success"))}),t.addEventListener("click",e=>{e.preventDefault(),r.classList.remove("form-error"),s.reset()})});